class port_info{	public:		double top;		double left;		double width;		double height;				port_info (double TOP, double LEFT, double WIDTH, double HEIGHT)		{			top=TOP;			left=LEFT;			width=WIDTH;			height=HEIGHT;		}				port_info (double WIDTH=500, double HEIGHT=500)		{			top=50;			left=50;			width=WIDTH;			height=HEIGHT;		}};class border_info{	public:		double top;		double bottom;		double left;		double right;				border_info (double TOP=10, double BOTTOM=20, double LEFT=30, double RIGHT=10)		{			top=TOP;			bottom=BOTTOM;			left=LEFT;			right=RIGHT;		}		};class bound_info{	public:		double top;		double bottom;		double left;		double right;		double width;		double height;				bound_info()		{			top=0;			bottom=0;			left=0;			right=0;			width=0;			height;		}};class scale_info{	public:		double MajScale;		double MinTicks;				scale_info (double MAJ=1, double MIN=5)		{			MajScale=MAJ;			MinTicks=MIN;		}				};class range_info{	public:		double Max;		double Min;				range_info (double MIN=-5, double MAX=5)		{			Max=MAX;			Min=MIN;		}				double range()		{			return Max-Min;		}};class tick_info{	public:		double MajTickSize;		double MinTickSize;				tick_info(double MAJ=5, double MIN=2)		{			MajTickSize=MAJ;			MinTickSize=MIN;		}};