/************************************************************************************** * HEADER FILE: graph_device.h * PURPOSE		: declare graph_device class **************************************************************************************/#include "extragraphclasses.h"#include <stdio.h>#ifndef _graph_device_h#define _graph_device_hclass graph_device{	private:		port_info port;																							// Window dimensions		border_info border;																					// Border sizes		bound_info bound;																						// Bound co-ordinates		scale_info horiz_scale, vert_scale;													// Horiz/Vert scale info		range_info horiz_range, vert_range;													// Horiz/Vert range info		tick_info ticks;																						// Tick size info				int scaleSize;																							// Text size in points				WindowPtr mainPtr;																					// Window Pointer			public:		graph_device();																							// Default constructor		// Parameterized constructor		graph_device(port_info newport, border_info newborder, scale_info newhoriz_scale,								 scale_info newvert_scale, range_info newhoriz_range,								 range_info newvert_range, tick_info newticks);		// Method for changing parameters after object created		void set_params(scale_info newhoriz_scale, scale_info newvert_scale,										range_info newhoriz_range,  range_info newvert_range);			// GUI functions		void showGraph();																							// Show window		void clear_window();																					// Clear window		void blank_graph();																						// Clear graph of plot			double translate_x(double x);											// Convert x-val to x graph co-ord		double translate_y(double y);											// Convert y-val to y graph co-ord		void draw_x_line(double x, long int brightness);	// Draw Vert line across x-axis		void draw_y_line(double y, long int brightness);	// Draw Horiz line  across y-axis		void draw_axes();																	// Draw axes on graph		void vertical_ticks();														// Add y-axis ticks to graph		void horizontal_ticks();													// Add x-axis ticks to graph		void PlaceCross(int Width);												// PlaceCross at current pen-pos		void set_port(port_info newport);									// Set port params		void set_border(border_info newborder);						// Set border params		void set_horiz_scales(scale_info newhoriz_scale);	// Set horiz scale params		void set_vert_scales(scale_info newvert_scale);		// Set vert scale params		void set_horiz_range(range_info newhoriz_range);	// Set horiz range params		void set_vert_range(range_info newvert_range);		// Set vert range params		void set_ticks(tick_info newticks);								// Set major/minor tick lengths		double x_origin(double x);												// Return origin coord, offset by x		double y_origin(double y);												// Return origin coord, offset by y};#endif